-- INSERT INTO `config_info` VALUES ('6', 'user-service.yml', 'DEFAULT_GROUP', 'spring:\n  datasource:\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://sduoj-mysql:3306/sduoj?useUnicode=true&characterEncoding=utf-8&useSSL=false\n    username: sduoj\n    password: sduoj\n    hikari:\n      minimum-idle: 5\n      maximum-pool-size: 15\n      idle-timeout: 30000\n  data:\n    redis:\n      host: sduoj-redis\n      password: \"\"\n      port: 6379\n      jedis:\n        pool:\n          max-idle: 8\n          max-wait: -1\n          min-idle: 0\n      timeout: 5000\n  rabbitmq:\n    host: sduoj-rabbit\n    port: 5672\n    username: sduoj\n    password: sduoj\n    virtual-host: /sduoj\n    publisher-confirms: true\n    listener:\n      simple:\n        prefetch: 1\n        concurrency: 2\n        max-concurrency: 4\n        default-requeue-rejected: true\n        retry:\n          initial-interval: 5000\n          enabled: true\n          max-attempts: 3\n\nSDUOJ:\n  site:\n    copyright: |\n      <hr style=\"max-width:1500px;width:100%;margin:0 auto;margin-bottom:29px;background-color:#e8eaec;height:1px;border:none;\" /> \n      <br>\n      <div style=\"color:#000000; display:flex;max-width:1000px;width:100%;margin:0 auto;\"> \n        <div style=\"width:100%;\"> \n          <h2 style=\"margin-bottom: 0\">NJU-Compiler Online Judge</h2> \n          <p style=\"margin-bottom: 0\">NJU-Compiler OJ is an online judge system </p> \n          <p style=\"margin-bottom: 0\">powered by <font size=\"0.7\"><a href=\"https://github.com/SDUOJ/OnlineJudge\" style=\"color:black;\">SDUOJ</a></font></p> \n        </div> \n        <div style=\"witdh:100%;\"> \n          <h2 style=\"margin-bottom: 0\">Supports</h2> \n          <p style=\"margin-bottom: 0\">Any bugs or crashes can be reported to <a href=\"mailto:未设置\">Admin Email</a></p> \n        </div> \n      </div>\n  mail:\n    enable: true # 是否启用邮件发送\n    username: 你的学号@smail.nju.edu.cn\n    password: 生成的客户端专用密码\n    host: smtp.exmail.qq.com\n    properties:\n      mail:\n        smtp:\n          ssl:\n            enable: false\n  user:\n    enableThirdPartySduCas: false\n    sduCasServiceUrl: https://oj.qd.sdu.edu.cn/v2/thirdPartyLogin?thirdParty=SDUCAS\n    enableEmailVerification: true # 是否要求邮箱是经过验证的。如果要求但邮件发送未启用，则注册、更改邮箱等业务将被禁用。如果不要求则用户随便输入邮箱即可\n    enableSendingEmailCode: true  # 是否启用邮箱验证码发送。如果启用但邮件发送为启用，则相关验证码业务被禁用。如果不要求，则随便输入验证码都将通过\n    enableForcedThirdPartyLogin: false # 当用户绑定了任一第三方账号时，是否强制要求第三方认证登录\n    allowEmailPatternList:\n      - .*?@smail\\.nju\\.edu\\.cn\n    sendEmailInterval: 30\n    verificationExpire: 500 # 校验码过期时间\n    maxPasswordAttempts: 10 # 最大密码尝试次数\n    userLockDurationInMinutes: 15 # 密码尝试失败后锁定账号的时长（单位：分钟）\n    allEmailBasicTemplate: |\n        <head>\n        </head>\n        <body>\n            <table align=\"center\" width=\"700\">\n                <tbody>\n                    <tr>\n                        <td>\n                            <table style=\"border-bottom:1px solid #ccc;margin-bottom:30px;\">\n                                <tbody>\n                                    <tr>\n                                    </tr>\n                                </tbody>\n                            </table>\n                            {{content}}\n                            <div\n                                style=\"margin-top:30;padding:10px 10px 0;border-top:1px solid #ccc;color:#747474;margin-bottom:20px;line-height:1.3em;font-size:12px;\">\n                                <p>\n                                    此为系统邮件，请勿回复\n                                    <br>\n                                    请保管好您的邮箱，避免账号被他人盗用\n                                </p>\n                                <p>2022-2024 &copy; NJU-Compiler OJ at Nanjing University</p>\n                            </div>\n                        </td>\n                    </tr>\n                </tbody>\n            </table>\n        </body>\n    verificationEmailSubject: \"[NJU-Compiler OJ] 邮箱验证码，请及时查收并注意账号安全\"\n    verificationEmailTemplate: |\n      <div style=\"padding:0 10px;line-height:1.5;font-size:14px;margin-bottom:25px;color:#000000;\">\n          <strong style=\"display:block;margin-bottom:15px;\">\n              尊敬的用户：\n              <span style=\"color:#f60;font-size: 16px;\"></span>\n          </strong>\n          <strong style=\"display:block;margin-bottom:15px;\">\n              您正在进行\n              <span style=\"color: red\">{{event}}</span>\n              操作，请在验证码输入框中输入以下验证码，以完成操作：\n              <div style=\"padding: 12px; background-color:#f5f5f5; color:#000000; font-weight:bold; color:#f60; font-size:24px;\" align=\"center\"> \n                  {{verificationCode}}\n              </div>\n          </strong>\n      </div>\n    forgetPasswordUrlPrefix: https://compiler.pascal-lab.net/resetpass?token=\n    forgetPasswordEmailSubject: \"[NJU-Compiler OJ] 账号忘记密码邮件，你的账号申请重置密码成功，请及时进行重置\"\n    forgetPasswordTemplate: |\n      <div style=\"padding:0 10px;line-height:1.5;font-size:14px;margin-bottom:25px;color:#000000;\">\n          <strong style=\"display:block;margin-bottom:15px;\">\n              尊敬的 {{username}} 用户：\n              <span style=\"color:#f60;font-size: 16px;\"></span>\n          </strong>\n          <strong style=\"display:block;margin-bottom:15px;\">\n              您正在进行\n              <span style=\"color: red\">重置密码</span>\n              操作，请点击以下链接对你的账号进行重置密码 ，以完成操作：\n              <div style=\"padding:12px; background-color:#f5f5f5; color:#000000; font-weight:bold; font-size:16px;\" align=\"center\"> \n                  <a href=\"{{forgetPasswordUrl}}\">{{forgetPasswordUrl}}</a> \n              </div>\n          </strong>\n      </div>\n    userLockEmailSubject: \"[NJU-Compiler OJ] 您的账号登录次数过多，已被锁定，请注意账号安全\"\n    userLockEmailTemplate: |\n      <div style=\"padding:0 10px;line-height:1.5;font-size:14px;margin-bottom:25px;color:#000000;\">\n          <strong style=\"display:block;margin-bottom:15px;\">\n              尊敬的 {{username}} 用户：\n              <span style=\"color:#f60;font-size: 16px;\"></span>\n          </strong>\n          <strong style=\"display:block;margin-bottom:15px;\">\n              您的账号由于登录失败次数过多，已被临时锁定。\n          </strong>\n          <p>\n              登录方信息：\n              <br>\n              时间：{{time}}\n              <br>\n              IP地址：{{ipv4}}\n              <br>\n              浏览器：{{userAgent}}\n              <br>\n          </p>\n          请注意保护您的账户和密码。如果您发现账户经常有异常活动，请联系管理员。\n      </div>\n', '37508303f1a51f4b9dad3de0842b6db5', '2023-02-13 20:27:09', '2024-03-19 14:06:23', 'sduoj', '127.0.0.1', 'sduoj', 'prod', '', '', '', 'yaml', '', '');
